    /**
     * find an error in data
     */
    public static void checkDb(){

        for (Place place : placeTable.all()) {

            List<Puzzle> puzzles = puzzlesTable.findPuzzlesByClubName(place.clubName);
            if(puzzles.size() != 5){
                Log.w("DB", String.format("Place %s (%s) doesnt have 5 places, only %s", place.clubName , place.category , puzzles.size()));
            }
        }

        Map<String, String> images = new HashMap<>();

        for (Puzzle puzzle : puzzlesTable.all()) {
            if(images.containsKey(puzzle.image)){
                Log.w("DB", String.format("Puzzle %s have duplicate image %s with %s", puzzle.name, puzzle.image, images.get(puzzle.image)));
            }else{
                images.put(puzzle.image, puzzle.name);
            }
        }

    }

